mapper.update:
update D_UC_BUSINESS_SYSTEM
set VERID = NVL(VERID , 0) + 1
  ,ICON_URL = #{iconUrl,jdbcType=VARCHAR}
  ,BANNER_URL = #{bannerUrl,jdbcType=VARCHAR}
  ,THEME = #{theme,jdbcType=VARCHAR}
  ,BASEPATH = #{basepath,jdbcType=VARCHAR}
  ,SYS_STATUS = #{sysStatus,jdbcType=DECIMAL}
  ,SYS_TYPE = #{sysType,jdbcType=DECIMAL}
  ,SYS_NAME = #{sysName,jdbcType=NVARCHAR}
  ,SYS_TITLE = #{sysTitle,jdbcType=NVARCHAR}
  ,SYS_DESC = #{sysDesc,jdbcType=NVARCHAR}
  ,SYS_ORDER = #{sysOrder,jdbcType=DECIMAL}
  ,UPDATETIME = CURRENT_TIMESTAMP
  ,UPDATEUSER = #{updateuser,jdbcType=CHAR}
where ID = #{id,jdbcType=CHAR}
