mapper.update:
update D_UC_ORG_PERSON
set VERID = NVL(VERID , 0) + 1
  ,ORG_ID = #{orgId,jdbcType=CHAR}
  ,PERSON_ID = #{personId,jdbcType=CHAR}
  ,ENTP_PERSON_TYPE = #{entpPersonType,jdbcType=DECIMAL}
  ,TEL = #{tel,jdbcType=VARCHAR}
  ,MOBILE = #{mobile,jdbcType=VARCHAR}
  ,MAIL = #{mail,jdbcType=VARCHAR}
  ,POSITION = #{position,jdbcType=VARCHAR}
  ,PERSON_STATUS = #{personStatus,jdbcType=DECIMAL}
  ,MEMO = #{memo,jdbcType=NVARCHAR}
  ,EXPIRY_DATE_STT = #{expiryDateStt,jdbcType=TIMESTAMP}
  ,EXPIRY_DATE_END = #{expiryDateEnd,jdbcType=TIMESTAMP}
  ,UPDATETIME = CURRENT_TIMESTAMP
  ,UPDATEUSER = #{updateuser,jdbcType=CHAR}
where ID = #{id,jdbcType=CHAR}
