mapper.update:
update D_UC_PERSON
set VERID = NVL(VERID , 0) + 1
  ,NAME = #{name,jdbcType=NVARCHAR}
  ,SEX = #{sex,jdbcType=DECIMAL}
  ,BIRTHDAY = #{birthday,jdbcType=TIMESTAMP}
  ,IDENTITY_TYPE = #{identityType,jdbcType=DECIMAL}
  ,IDENTITY_ID = #{identityId,jdbcType=VARCHAR}
  ,IDENTITY_URL = #{identityUrl,jdbcType=VARCHAR}
  ,PHOTO_URL = #{photoUrl,jdbcType=VARCHAR}
  ,TEL = #{tel,jdbcType=VARCHAR}
  ,MOBILE = #{mobile,jdbcType=VARCHAR}
  ,FAX = #{fax,jdbcType=VARCHAR}
  ,MAIL = #{mail,jdbcType=VARCHAR}
  ,PROVINCE = #{province,jdbcType=CHAR}
  ,CITY = #{city,jdbcType=CHAR}
  ,DISTRICT = #{district,jdbcType=CHAR}
  ,ADDRESS = #{address,jdbcType=VARCHAR}
  ,PERSON_STATUS = #{personStatus,jdbcType=DECIMAL}
  ,MEMO = #{memo,jdbcType=NVARCHAR}
  ,UPDATETIME = CURRENT_TIMESTAMP
  ,UPDATEUSER = #{updateuser,jdbcType=CHAR}
where ID = #{id,jdbcType=CHAR}
